const Map<String, String> _styleToBucket = {
  'Rock': 'rock',
  'Pop': 'pop',
  'Indie': 'indie',
  'Funk': 'funkSoulRb',
  'Soul': 'funkSoulRb',
  'R&B': 'funkSoulRb',
  'Jazz': 'jazzBlues',
  'Blues': 'jazzBlues',
  'Hip-Hop': 'hipHopRapTrap',
  'Rap': 'hipHopRapTrap',
  'Trap': 'hipHopRapTrap',
  'Electrónica': 'electronicHouseTechno',
  'House': 'electronicHouseTechno',
  'Techno': 'electronicHouseTechno',
  'Reggae': 'reggaeSkaPunk',
