  'Country': 'country',
};

final Map<String, List<String>> _parsedBucketCache = {};

List<String> affinityArtistOptionsForStyle(String? style) {
  if (style == null) {
    return const [];
  }

  final bucket = _styleToBucket[style.trim()];
  if (bucket == null) {
    return const [];
  }

  return _parsedBucketCache.putIfAbsent(bucket, () {
    final raw = _bucketArtistsRaw[bucket] ?? '';
    return List.unmodifiable(_parseArtists(raw));
  });
}

List<String> _parseArtists(String raw) {
  final parsed = <String>[];
  final seen = <String>{};

  for (final token in raw.split('|')) {
    var artist = token.trim();
    if (artist.isEmpty) {
      continue;
    }

    artist = artist.replaceFirst(RegExp(r'^\d+\.\s*'), '').trim();
    artist = artist.replaceFirst(RegExp(r'^[•·-]\s*'), '').trim();
    if (artist.endsWith('.')) {
      artist = artist.substring(0, artist.length - 1).trim();
    }

    if (artist.isEmpty) {
      continue;
    }

    final key = artist.toLowerCase();
    if (seen.add(key)) {
      parsed.add(artist);
    }
  }

  return parsed;
}
